     4→class ConfigLoader {
     5→  constructor(configPath) {
     6→    this.configPath = configPath || path.join(__dirname, '..', 'config.json');
     7→    this.config = null;
     8→  }
     9→
    10→  load() {
    11→    try {
    12→      const configData = fs.readFileSync(this.configPath, 'utf8');
    13→      this.config = JSON.parse(configData);
    14→      this.validate();
    15→      return this.config;
    16→    } catch (error) {
    17→      if (error.code === 'ENOENT') {
    18→        throw new Error(`Configuration file not found at ${this.configPath}`);
    19→      } else if (error instanceof SyntaxError) {
    20→        throw new Error(`Invalid JSON in configuration file: ${error.message}`);
    21→      }
    22→      throw error;
    23→    }
    24→  }
    25→
    26→  reload() {
    27→    try {
    28→      const configData = fs.readFileSync(this.configPath, 'utf8');
    29→      const newConfig = JSON.parse(configData);
    30→      
    31→      // Store old config in case validation fails
    32→      const oldConfig = this.config;
    33→      this.config = newConfig;
    34→      
    35→      try {
    36→        this.validate();
