     1→class RollupManager {
     2→  constructor(database, logger) {
     3→    this.database = database;
     4→    this.logger = logger;
     5→    
     6→    // Define rollup hierarchy - each level uses the previous as source
     7→    this.rollupIntervals = [
     8→      { key: '5m', seconds: 5 * 60, source: 'metrics', maxEntries: 120 },
     9→      { key: '30m', seconds: 30 * 60, source: '5m', maxEntries: 120 },
    10→      { key: '1h', seconds: 60 * 60, source: '30m', maxEntries: 120 },
    11→      { key: '12h', seconds: 12 * 60 * 60, source: '1h', maxEntries: 120 },
    12→      { key: '1d', seconds: 24 * 60 * 60, source: '12h', maxEntries: 120 },
    13→      { key: '1mo', seconds: 30 * 24 * 60 * 60, source: '1d', maxEntries: 120 }
    14→    ];
    15→  }
    16→
    17→  start() {
    18→    this.logger.info('Rollup manager started - will calculate rollups on metric capture');
    19→  }
    20→
    21→  async calculateRollupsForMetrics(metrics) {
    22→    const uniqueKeys = new Set();
    23→    metrics.forEach(m => uniqueKeys.add(`${m.key}:${m.name}`));
    24→    
    25→    for (const keyName of uniqueKeys) {
    26→      const [key, name] = keyName.split(':');
    27→      await this.calculateRollupsForMetric(key, name);
    28→    }
    29→  }
    30→
    31→  async calculateRollupsForMetric(key, name) {
    32→    const now = Math.floor(Date.now() / 1000);
    33→    
    34→    for (const interval of this.rollupIntervals) {
    35→      try {
    36→        await this.updateRollup(interval, key, name, now);
    37→      } catch (error) {
    38→        this.logger.error(`Error calculating ${interval.key} rollup for ${key}/${name}: ${error.message}`);
    39→      }
    40→    }
    41→  }
    42→
    43→  async updateRollup(interval, key, name, currentTime) {
    44→    const alignedTimestamp = this.alignTimestamp(currentTime, interval.seconds);
    45→    const startTime = alignedTimestamp;
    46→    const endTime = alignedTimestamp + interval.seconds;
    47→    
    48→    let data, unit;
    49→    
    50→    if (interval.source === 'metrics') {
    51→      // First level uses raw metrics
    52→      const metrics = await this.database.getMetricsForRollup(key, name, startTime, endTime);
    53→      if (metrics.length === 0) return;
    54→      
    55→      const values = metrics.map(m => m.value);
    56→      const min = Math.min(...values);
    57→      const max = Math.max(...values);
    58→      const average = values.reduce((a, b) => a + b, 0) / values.length;
    59→      
    60→      // Get unit from metrics
    61→      const firstMetric = await this.database.all(
    62→        'SELECT unit FROM metrics WHERE key = ? AND name = ? LIMIT 1',
    63→        [key, name]
    64→      );
    65→      unit = firstMetric[0]?.unit || null;
    66→      
    67→      data = { min, max, average };
    68→    } else {
    69→      // Higher levels use previous rollup level
    70→      const sourceData = await this.database.all(
    71→        `SELECT min, max, average, unit 
    72→         FROM rollups 
    73→         WHERE increment = ? AND key = ? AND name = ? 
    74→         AND timestamp >= ? AND timestamp < ?`,
    75→        [interval.source, key, name, startTime, endTime]
    76→      );
    77→      
    78→      if (sourceData.length === 0) return;
    79→      
    80→      // Aggregate from source rollups
    81→      const mins = sourceData.map(d => d.min);
    82→      const maxs = sourceData.map(d => d.max);
    83→      const averages = sourceData.map(d => d.average);
    84→      
    85→      const min = Math.min(...mins);
    86→      const max = Math.max(...maxs);
    87→      const average = averages.reduce((a, b) => a + b, 0) / averages.length;
    88→      
    89→      unit = sourceData[0]?.unit || null;
    90→      data = { min, max, average };
    91→    }
    92→    
    93→    // Insert or update the rollup
    94→    await this.database.insertRollup(
    95→      interval.key,
    96→      alignedTimestamp,
    97→      key,
    98→      name,
    99→      unit,
   100→      data.min,
   101→      data.max,
   102→      data.average
   103→    );
   104→    
   105→    // Cleanup old data
   106→    await this.cleanupOldData(interval, key, name, currentTime);
   107→  }
   108→
   109→  async cleanupOldData(interval, key, name, currentTime) {
   110→    // Calculate cutoff time based on max entries
   111→    const cutoffTime = currentTime - (interval.maxEntries * interval.seconds);
   112→    
   113→    if (interval.source === 'metrics') {
   114→      // For 5m rollup, clean up raw metrics older than 1 hour
   115→      const metricsKeepTime = currentTime - (60 * 60); // Keep last hour
   116→      await this.database.all(
   117→        'DELETE FROM metrics WHERE key = ? AND name = ? AND timestamp < ?',
   118→        [key, name, metricsKeepTime]
   119→      );
   120→    }
   121→    
   122→    // Clean up old rollups for this interval
   123→    await this.database.all(
   124→      'DELETE FROM rollups WHERE increment = ? AND key = ? AND name = ? AND timestamp < ?',
   125→      [interval.key, key, name, cutoffTime]
   126→    );
   127→  }
   128→
   129→  alignTimestamp(timestamp, intervalSeconds) {
   130→    return Math.floor(timestamp / intervalSeconds) * intervalSeconds;
   131→  }
   132→
   133→  stop() {
   134→    this.logger.info('Rollup manager stopped');
   135→  }
