      { key: '12h', seconds: 12 * 60 * 60, source: '1h', maxEntries: 120 },
      { key: '1d', seconds: 24 * 60 * 60, source: '12h', maxEntries: 120 },
      { key: '1mo', seconds: 30 * 24 * 60 * 60, source: '1d', maxEntries: 120 }
    ];
