   111→  private async fetchChartData(title: string, config: GraphConfig): Promise<ChartData[]> {
   112→    const metricKey = config.metric === '*' ? 'all' : config.metric;
   113→    const yAxisName = config['y-axis'];
   114→    const now = Math.floor(Date.now() / 1000);
