     1→import type winston from 'winston';
     2→import type Database from './database.ts';
     3→
     4→interface RollupInterval {
     5→  key: string;
     6→  seconds: number;
     7→  source: string;
     8→  maxEntries: number;
     9→}
    10→
    11→interface MetricInfo {
    12→  key: string;
    13→  name: string;
    14→}
    15→
    16→interface RollupData {
    17→  min: number;
    18→  max: number;
    19→  average: number;
    20→}
    21→
    22→class RollupManager {
    23→  private database: Database;
    24→  private logger: winston.Logger;
    25→  private rollupIntervals: RollupInterval[];
    26→
    27→  constructor(database: Database, logger: winston.Logger) {
    28→    this.database = database;
    29→    this.logger = logger;
    30→    
    31→    // Define rollup hierarchy - each level uses the previous as source
    32→    this.rollupIntervals = [
    33→      { key: '5m', seconds: 5 * 60, source: 'metrics', maxEntries: 120 },
    34→      { key: '30m', seconds: 30 * 60, source: '5m', maxEntries: 120 },
    35→      { key: '1h', seconds: 60 * 60, source: '30m', maxEntries: 120 },
    36→      { key: '12h', seconds: 12 * 60 * 60, source: '1h', maxEntries: 120 },
    37→      { key: '1d', seconds: 24 * 60 * 60, source: '12h', maxEntries: 120 },
    38→      { key: '1mo', seconds: 30 * 24 * 60 * 60, source: '1d', maxEntries: 120 }
    39→    ];
    40→  }
    41→
    42→  start(): void {
    43→    this.logger.info('Rollup manager started - will calculate rollups on metric capture');
    44→  }
    45→
    46→  async calculateRollupsForMetrics(metrics: MetricInfo[]): Promise<void> {
    47→    const uniqueKeys = new Set<string>();
    48→    metrics.forEach(m => uniqueKeys.add(`${m.key}:${m.name}`));
    49→    
    50→    for (const keyName of uniqueKeys) {
    51→      const [key, name] = keyName.split(':');
    52→      await this.calculateRollupsForMetric(key, name);
    53→    }
    54→  }
    55→
    56→  async calculateRollupsForMetric(key: string, name: string): Promise<void> {
    57→    const now = Math.floor(Date.now() / 1000);
    58→    
    59→    for (const interval of this.rollupIntervals) {
    60→      try {
    61→        await this.updateRollup(interval, key, name, now);
    62→      } catch (error: any) {
    63→        this.logger.error(`Error calculating ${interval.key} rollup for ${key}/${name}: ${error.message}`);
    64→      }
    65→    }
    66→  }
    67→
    68→  private async updateRollup(interval: RollupInterval, key: string, name: string, currentTime: number): Promise<void> {
    69→    const alignedTimestamp = this.alignTimestamp(currentTime, interval.seconds);
    70→    const startTime = alignedTimestamp;
    71→    const endTime = alignedTimestamp + interval.seconds;
    72→    
    73→    let data: RollupData;
    74→    let unit: string | undefined;
    75→    
    76→    if (interval.source === 'metrics') {
    77→      // First level uses raw metrics
    78→      const metrics = await this.database.getMetricsForRollup(key, name, startTime, endTime);
    79→      if (metrics.length === 0) return;
    80→      
    81→      const values = metrics.map(m => m.value);
    82→      const min = Math.min(...values);
    83→      const max = Math.max(...values);
    84→      const average = values.reduce((a, b) => a + b, 0) / values.length;
    85→      
    86→      // Get unit from metrics
    87→      const firstMetric = await (this.database as any).all(
    88→        'SELECT unit FROM metrics WHERE key = ? AND name = ? LIMIT 1',
    89→        [key, name]
    90→      );
    91→      unit = firstMetric[0]?.unit || undefined;
    92→      
    93→      data = { min, max, average };
    94→    } else {
    95→      // Higher levels use previous rollup level
    96→      const sourceData = await (this.database as any).all(
    97→        `SELECT min, max, average, unit 
    98→         FROM rollups 
    99→         WHERE increment = ? AND key = ? AND name = ? 
   100→         AND timestamp >= ? AND timestamp < ?`,
   101→        [interval.source, key, name, startTime, endTime]
   102→      );
   103→      
   104→      if (sourceData.length === 0) return;
   105→      
   106→      // Aggregate from source rollups
   107→      const mins = sourceData.map((d: any) => d.min);
   108→      const maxs = sourceData.map((d: any) => d.max);
   109→      const averages = sourceData.map((d: any) => d.average);
   110→      
   111→      const min = Math.min(...mins);
   112→      const max = Math.max(...maxs);
   113→      const average = averages.reduce((a: number, b: number) => a + b, 0) / averages.length;
   114→      
   115→      unit = sourceData[0]?.unit || undefined;
   116→      data = { min, max, average };
   117→    }
   118→    
   119→    // Insert or update the rollup
   120→    await this.database.insertRollup(
   121→      interval.key,
   122→      alignedTimestamp,
   123→      key,
   124→      name,
   125→      unit,
   126→      data.min,
   127→      data.max,
   128→      data.average
   129→    );
   130→    
   131→    // Cleanup old data
   132→    await this.cleanupOldData(interval, key, name, currentTime);
   133→  }
   134→
   135→  private async cleanupOldData(interval: RollupInterval, key: string, name: string, currentTime: number): Promise<void> {
   136→    // Calculate cutoff time based on max entries
   137→    const cutoffTime = currentTime - (interval.maxEntries * interval.seconds);
   138→    
   139→    if (interval.source === 'metrics') {
   140→      // For 5m rollup, clean up raw metrics older than 1 hour
   141→      const metricsKeepTime = currentTime - (60 * 60); // Keep last hour
   142→      await (this.database as any).all(
   143→        'DELETE FROM metrics WHERE key = ? AND name = ? AND timestamp < ?',
   144→        [key, name, metricsKeepTime]
   145→      );
   146→    }
