   348→  private getColor(index: number, opacity = 1): string {
   349→    const colors = [
   350→      [75, 192, 192],
   351→      [255, 99, 132],
   352→      [54, 162, 235],
   353→      [255, 205, 86],
   354→      [153, 102, 255],
