   359→      ? `rgb(${color[0]}, ${color[1]}, ${color[2]})` 
   360→      : `rgba(${color[0]}, ${color[1]}, ${color[2]}, ${opacity})`;
   361→  }
   362→
   363→  private formatTimestamp(timestamp: number): string {
   364→    const date = new Date(timestamp * 1000);
   365→    const now = new Date();
   366→    
   367→    if (date.toDateString() === now.toDateString()) {
   368→      return format(date, 'HH:mm');
   369→    }
   370→    
   371→    return format(date, 'MMM dd HH:mm');
   372→  }
   373→
   374→  private generateTimeLabels(startTime: number, endTime: number, source: string): string[] {
   375→    const labels: string[] = [];
   376→    let interval: number;
   377→    
   378→    if (source === 'latest') {
   379→      interval = 60; // 1 minute
   380→    } else if (source === 'rollup-5m') {
   381→      interval = 5 * 60; // 5 minutes
   382→    } else if (source === 'rollup-30m') {
   383→      interval = 30 * 60; // 30 minutes
   384→    } else if (source === 'rollup-1h') {
   385→      interval = 60 * 60; // 1 hour
   386→    } else if (source === 'rollup-12h') {
   387→      interval = 12 * 60 * 60; // 12 hours
   388→    } else if (source === 'rollup-1d') {
   389→      interval = 24 * 60 * 60; // 1 day
   390→    } else {
   391→      interval = 60; // default to 1 minute
   392→    }
   393→    
   394→    // Align start time to interval
   395→    const alignedStart = Math.floor(startTime / interval) * interval;
   396→    
   397→    for (let t = alignedStart; t <= endTime; t += interval) {
   398→      labels.push(this.formatTimestamp(t));
   399→    }
   400→    
   401→    return labels;
   402→  }
   403→
   404→  private parseFormattedTimestamp(formatted: string): number {
   405→    // This is a simplified reverse of formatTimestamp
   406→    // In production, we'd want a more robust solution
   407→    const now = new Date();
   408→    const today = now.toDateString();
   409→    
   410→    // Check if it's today's format (HH:mm)
   411→    if (formatted.match(/^\d{2}:\d{2}$/)) {
   412→      const [hours, minutes] = formatted.split(':').map(Number);
   413→      const date = new Date();
   414→      date.setHours(hours, minutes, 0, 0);
   415→      return Math.floor(date.getTime() / 1000);
   416→    }
   417→    
   418→    // Otherwise parse full format (MMM dd HH:mm)
   419→    // This is a simplified parser - in production use a proper date parser
   420→    return 0; // Placeholder
   421→  }
   422→
   423→  private alignDataToFullTimeRange(
   424→    values: Array<{ timestamp: number; value: number }>,
   425→    labels: string[],
   426→    startTime: number,
   427→    endTime: number
   428→  ): (number | null)[] {
   429→    // Create a map for efficient lookup
   430→    const valueMap: Record<number, number> = {};
   431→    values.forEach(v => {
   432→      valueMap[v.timestamp] = v.value;
   433→    });
   434→    
   435→    // Generate data points for each label
   436→    return labels.map(label => {
   437→      // Find the timestamp that corresponds to this label
   438→      // This is simplified - in production we'd need proper timestamp parsing
   439→      for (const v of values) {
   440→        if (this.formatTimestamp(v.timestamp) === label) {
   441→          return v.value;
   442→        }
   443→      }
   444→      return null; // No data for this time point
   445→    });
   446→  }
   447→
   448→  private getStartTime(limit: string, now: number): number {
   449→    const units: Record<string, number> = {
   450→      's': 1,
   451→      'm': 60,
   452→      'h': 3600,
   453→      'd': 86400,
   454→      'mo': 2592000,
   455→      'y': 31536000
   456→    };
   457→    
   458→    const match = limit.match(/^(\d+)([smhdy]|mo)$/);
   459→    if (!match) return 0;
   460→    
   461→    const value = parseInt(match[1], 10);
   462→    const unit = match[2];
   463→    
   464→    return now - (value * units[unit]);
   465→  }
   466→
   467→
   468→  private sanitizeFilename(title: string): string {
   469→    return title.toLowerCase()
   470→      .replace(/[^a-z0-9]+/g, '-')
